import { useState } from "react"
import React  from 'react'



export default function Card(prop) {
    const [day, setDays] = useState(0);
    var myfunc = setInterval(function() {
        var countDownDate1 =new Date(prop.dob).getTime()
        var now = new Date().getTime();
        var timeleft = countDownDate1 - now;
        
    
        // Calculating the days, hours, minutes and seconds left
        var days = Math.floor(timeleft / (1000 * 60 * 60 * 24));
        var hours = Math.floor((timeleft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        var minutes = Math.floor((timeleft % (1000 * 60 * 60)) / (1000 * 60));
        var seconds = Math.floor((timeleft % (1000 * 60)) / 1000);
        setDays(day=>days)

    },1000)
  return (

<div className="col-md-3">

<div className="card" style={{width: "18rem"}}>
  <img src={prop.link} style={{height: "20rem"}} className="card-img-top" alt="..."/>
  <div className="card-body">
    <h5 className="card-title">{prop.name} </h5>
    <p className="card-text">Birth Day in : {day} Days </p>
    <a href="#" className="btn btn-primary">Go somewhere</a>
  </div>
</div>

</div>
  );}
